const addToDb = (id) => {
    let cart = {};

    // const storedCart = localStorage.getItem('cart');
    // if (storedCart) {
    //     cart = JSON.parse(storedCart);
    //     console.log(cart);
    // }

    let quantity = cart[id];
    if (quantity) {
        const newQuantity = quantity + 1;
        quantity[id] = newQuantity;
    }
    else {
        quantity[id] = 1;
    }
    console.log(cart);
    // localStorage.setItem('cart', JSON.stringify(cart));
}

export {
    addToDb
}
