import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import './Cart.css'

const Cart = (props) => {
    const { cart } = props;



    return (
        <div className='cart'>
            <h1 style={{ textAlign: 'center' }}>Cart</h1>
            {
                cart.map(product => <li key={product.idMeal}>{product.srtMeal}</li>)
            }
            <button>Clear <FontAwesomeIcon icon={faTrash}></FontAwesomeIcon> </button>
        </div>
    );
};

export default Cart;
