import React, { useEffect, useState } from 'react';
import Cart from '../Cart/Cart';
import Meal from '../Meal/Meal';
import { addToDb, getCart } from '../Utilities/Fakedb';
import './main.css'

const Main = () => {
    const [meals, setMeals] = useState([])
    const [inCart, setInCart] = useState([])

    useEffect(() => {
        fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=f`)
            .then(res => res.json())
            .then(data => setMeals(data.meals))
    }, [])

    useEffect(() => {
        const cart = getCart();
        const savedCart = [];
        for (const id in cart) {
            const addedMeal = meals.find(meal => meal.idMeal === id);
            if (addedMeal) {
                const quantity = cart[id];
                cart.quantity = quantity;
                savedCart.push(addedMeal);
            }

        }
        setInCart(savedCart);

    }, [meals])

    const clickHandel = (id) => {
        addToDb(id.idMeal);
    }
    return (
        <div className='main'>
            <div className='meals'>
                <h1 style={{ textAlign: 'center' }}>Total Meal : {meals.length}</h1>
                <div className="allMeals">
                    {
                        meals.map(meal => <Meal handel={clickHandel} key={meal.idMeal} meal={meal}></Meal>)
                    }
                </div>
            </div>
            <div className="cartSection">
                <Cart meals={meals} name={inCart[0]}></Cart>
            </div>
        </div>
    );
};

export default Main;
