import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import './Cart.css'

const Cart = (props) => {
    const { cart } = props;

    return (
        <div className='cart'>
            <h1 style={{ textAlign: 'center' }}>Cart</h1>
            <h4> Selected Item</h4>
            <ol>
                {
                    cart.map(product => <Items key={product.idMeal} name={product.srtMeal}></Items>)
                }
            </ol>
            <button>Clear <FontAwesomeIcon icon={faTrash}></FontAwesomeIcon> </button>
        </div>
    );
};

const Items = (props) => {
    console.log(props.name);
    return (
        <li></li>
    );

}

export default Cart;
