const addToDb = (id) => {
    let cart = {};

    const storedCart = localStorage.getItem('cart');
    if (storedCart) {
        cart = JSON.parse(storedCart);
    }

    let quantity = cart[id];
    if (quantity) {
        const newQuantity = quantity + 1;
        cart[id] = newQuantity;
    }
    else {
        cart[id] = 1;
    }
    localStorage.setItem('cart', JSON.stringify(cart));
}

const getQuantity = () => {
    const storedCart = localStorage.getItem('cart');
    const cart = JSON.parse(storedCart);
    for (const i in cart) {
        const value = [i];
        console.log(value);

    }
}

export {
    addToDb,
    getQuantity
}
