import React, { useEffect, useState } from 'react';
import Cart from '../Cart/Cart';
import Meal from '../Meal/Meal';
import { addToDb, getQuantity } from '../Utilities/Fakedb';
import './main.css'

const Main = () => {
    const [meals, setMeals] = useState([])
    const [names, setQNames] = useState([])

    useEffect(() => {
        fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=f`)
            .then(res => res.json())
            .then(data => setMeals(data.meals))
    }, [])

    const clickHandel = (id) => {
        let allnames = [];
        const exists = names.find(name => name.strMeal === id.strMeal);
        if (exists) {
            const mealName = id
            allnames = [...names, id]
        }
        else {
            allnames = [id]
        }
        console.log(allnames);

        // addToDb(id.idMeal);
    }
    return (
        <div className='main'>
            <div className='meals'>
                <h1 style={{ textAlign: 'center' }}>Total Meal : {meals.length}</h1>
                <div className="allMeals">
                    {
                        meals.map(meal => <Meal handel={clickHandel} key={meal.idMeal} meal={meal}></Meal>)
                    }
                </div>
            </div>
            <div className="cartSection">
                <Cart meals={meals}></Cart>
            </div>
        </div>
    );
};

export default Main;
